mapper.select:
SELECT NVL(COUNT(*), 0)
FROM INSTA_HASH_SUB
WHERE HASH_SEQ = #{hash_seq}

  		SELECT SEQ, MEMBER_SEQ, MEMBER_ID, IMAGE, PEED_LIKE, REGI_DATE, CONTENT
		FROM(
			SELECT ROW_NUMBER() OVER (ORDER BY REGI_DATE DESC) AS RN, SEQ, MEMBER_SEQ, MEMBER_ID, IMAGE, PEED_LIKE, REGI_DATE, CONTENT
			FROM(
				SELECT ROW_NUMBER() OVER (PARTITION BY PEED.SEQ ORDER BY PEED.REGI_DATE ASC) as rnum, PEED.SEQ,PEED.MEMBER_SEQ,PEED.MEMBER_ID,PEED.IMAGE,PEED.PEED_LIKE,PEED.REGI_DATE,PEED.CONTENT
				FROM INSTA_HASH_SUB HASH_SUB JOIN INSTA_PEED PEED
				ON HASH_SUB.HASH_SEQ = #{hash_seq}
			) A
			WHERE rnum = 1
		)
